mapper.update:
update tbl_schedule
set s_fromDate = #{s_fromDate}, s_toDate=#{s_toDate}, s_title=#{s_title}, s_content = #{s_content}, s_attendee=#{s_attendee}, s_place=#{s_place}, s_type=#{s_type}
where sno = #{sno}

mapper.select:
select * 
from tbl_schedule
where sno =#{sno} and s_type = #{s_type}

select * from tbl_schedule
where sno > 0 and s_id = #{s_id}
order by sno desc
limit #{pageStart}, #{perPageNum}

select count(sno)
from tbl_schedule
where sno > 0 and s_id = #{s_id}
